import styled from "styled-components";

const Container = styled.div`
  width: 100%;
  height: 100vh;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 50px;
  background-color: #f2f2f2;
  box-sizing: border-box;
  font-family: "Lexend Deca";
`;

const TitleHabits = styled.div`
  width: 90%;
  align-items: center;
  display: flex;
  justify-content: space-between;

  & h1 {
    font-size: 1.5em;
    color: #126ba5;
  }
  & button {
    font-size: 2em;
    font-weight: 500;
    display: flex;
    align-items: center;
    justify-content: center;
    width: 10%;
    height: 4vh;
    background-color: #52b6ff;
    color: #ffffff;
    border: none;
    border-radius: 5px;
    cursor: pointer;
  }
`;

const FormHabits = styled.form`
  width: 90%;
  border-radius: 5px;
  padding: 10px;
  background-color: #ffffff;

  & input {
  }
`;

const Text = styled.div`
  width: 90%;
  font-size: 1.1em;
  color: #666666;
`;

export { Container, TitleHabits, Text, FormHabits };
