import logo from "../../assets/images/logo.png";
import { Container } from "./styles";
import { Redirection } from "./styles";
import { Form } from "./styles";
import { useState } from "react";
import { ThreeDots } from "react-loader-spinner";
import axios from "axios";
import { useNavigate } from "react-router-dom";
export default function SignUp() {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [image, setImage] = useState("");
  const [isDiseblad, setIsDiseblad] = useState(false);
  const navigate = useNavigate();

  function handleSignUp(event) {
    event.preventDefault();
    setIsDiseblad(true);
    const data = {
      email,
      name,
      image,
      password,
    };

    const promise = axios.post(
      "https://mock-api.bootcamp.respondeai.com.br/api/v2/trackit/auth/sign-up",
      data
    );
    promise.then(navigate("/"));
    promise.catch((error) => {
      alert("Aconteceu algo errado, tente novamente D:");
      setIsDiseblad(false);
    });
  }
  return (
    <Main>
      <img src={logo} alt="logo" />
      <FormTemplate onSubmit={handleSignUp}>
        <input
          type="name"
          placeholder="nome"
          onChange={(event) => {
            setName(event.target.value);
          }}
          value={name}
          disabled={isDiseblad}
        />
        <input
          type="email"
          placeholder="email"
          onChange={(event) => {
            setEmail(event.target.value);
          }}
          value={email}
          disabled={isDiseblad}
        />
        <input
          type="password"
          value={password}
          placeholder="senha"
          onChange={(event) => {
            setPassword(event.target.value);
          }}
          disabled={isDiseblad}
        />
        <input
          type="url"
          value={image}
          placeholder="foto"
          onChange={(event) => {
            setImage(event.target.value);
          }}
          disabled={isDiseblad}
        />
        <button disabled={isDiseblad}>
          {isDiseblad ? (
            <ThreeDots color="#ffffff" height={50} width={50} />
          ) : (
            "Cadastrar"
          )}
        </button>
      </FormTemplate>
      <Anchor to="/">Já tem uma conta? Faça login!</Anchor>
    </Main>
  );
}
