import styled from "styled-components";
export default function FormTemplate({ children }) {
  return <Form></Form>;
}

const Form = styled.form`
  width: 80%;
  gap: 10px;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;

  & input {
    width: 100%;
    height: 45px;
    font-size: 1.2em;
    outline: none;
    background: #ffffff;
    border: 1px solid #d5d5d5;
    border-radius: 5px;
    padding: 0 10px;
    font-weight: 400;
    font-size: 19.976px;
    line-height: 25px;
    ::placeholder {
      font-weight: 600;
      font-size: 19.976px;
      line-height: 25px;
      color: #dbdbdb;
    }
    :disabled {
      color: #afafaf;
      background-color: #f2f2f2;
    }
  }
  & button {
    all: unset;
    width: 100%;
    height: 45px;
    font-size: 1.5em;
    display: flex;
    justify-content: center;
    align-items: center;
    color: #ffffff;
    background: #52b6ff;
    border-radius: 4px;
    text-align: center;
    padding: 0 10px;
    cursor: pointer;
    :hover {
      filter: brightness(1.1);
    }
  }
`;
