import styled from "styled-components";

const Container = styled.div`
  box-sizing: border-box;
  font-family: "Lexend Deca";
  height: 100vh;

  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
`;

const Form = styled.form`
  width: 80%;
  gap: 10px;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;

  & input {
    width: 100%;
    height: 45px;
    font-size: 1.2em;
    outline: none;
    background: #ffffff;
    border: 1px solid #d5d5d5;
    border-radius: 5px;

    ::placeholder {
      padding: 0 10px;
    }
  }
  & button {
    all: unset;
    width: 100%;
    height: 45px;
    background: #52b6ff;
    border-radius: 4px;
  }
`;

export { Container, Form };
