import { Container, Form, Redirection } from "./styles";
import logo from "../../assets/images/logo.png";
import { useState } from "react";
import { ThreeDots } from "react-loader-spinner";
export default function SignUp() {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [image, setImage] = useState("");
  const [isDiseblad, setIsDiseblad] = useState(false);

  function handleSignUp(event) {
    event.preventDefault();
    setIsDiseblad(true);
    console.log(name);
    console.log(email);
    console.log(password);
    console.log(image);
  }
  return (
    <Container>
      <img src={logo} alt="logo" />
      <Form onSubmit={handleSignUp}>
        <input
          type="name"
          placeholder="nome"
          onChange={(event) => {
            setName(event.target.value);
          }}
          value={name}
          disabled={isDiseblad}
        />
        <input
          type="email"
          placeholder="email"
          onChange={(event) => {
            setEmail(event.target.value);
          }}
          value={email}
          disabled={isDiseblad}
        />
        <input
          type="password"
          value={password}
          placeholder="senha"
          onChange={(event) => {
            setPassword(event.target.value);
          }}
          disabled={isDiseblad}
        />
        <input
          type="url"
          value={image}
          placeholder="foto"
          onChange={(event) => {
            setImage(event.target.value);
          }}
          disabled={isDiseblad}
        />
        <button disabled={isDiseblad}>
          {isDiseblad ? (
            <ThreeDots color="#ffffff" height={50} width={50} />
          ) : (
            "Cadastrar"
          )}
        </button>
      </Form>
      <Redirection to="/">Já tem uma conta? Faça login!</Redirection>
    </Container>
  );
}
