import styled from "styled-components";

const Container = styled.div`
  width: 100%;
  height: 100vh;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 50px;
  background-color: #f2f2f2;
  box-sizing: border-box;
  font-family: "Lexend Deca";
`;

const TitleHabits = styled.div`
