`;

const ContainerButtons = styled.div`
  display: flex;
  justify-content: right;
  width: 40%;
`;

const ButtonSave = styled.button`
  width: 40%;
  height: 3vh;
`;

const Text = styled.div`
  width: 90%;
  font-size: 1.1em;
  color: #666666;
`;

export { Container, TitleHabits, Text, FormHabits, DaysButtons };
