import React from "react";
import { Container, Form, Redirection } from "./styles";
import logo from "../../assets/images/logo.png";
import { useState } from "react";
import { ThreeDots } from "react-loader-spinner";
import axios from "axios";
import { useNavigate } from "react-router-dom";
export default function Login() {
  const [password, setPassword] = useState("");
  const [email, setEmail] = useState("");
  const [isDiseblad, setIsDiseblad] = useState(false);
  const navigate = useNavigate();

  function handleLogin(event) {
    event.preventDefault();
    setIsDiseblad(true);
    const data = {
      email,
      password,
    };

    const promise = axios.post(
      "https://mock-api.bootcamp.respondeai.com.br/api/v2/trackit/auth/login",
      data
    );
    promise.then(navigate("/habitos"));
    promise.catch(() => {
      alert("Email ou senhas incorretos, tente novamente.");
      setIsDiseblad(true);
    });
  }

  return (
    <Container>
      <img src={logo} alt="logo" />
      <Form onSubmit={handleLogin}>
        <input
          disabled={isDiseblad}
          type="email"
          value={email}
          onChange={(event) => {
            setEmail(event.target.value);
          }}
          placeholder="email"
        />
        <input
          disabled={isDiseblad}
          type="password"
          value={password}
          onChange={(event) => {
            setPassword(event.target.value);
          }}
          placeholder="senha"
        />
        <button disabled={isDiseblad}>
          {isDiseblad ? (
            <ThreeDots color="#ffffff" height={50} width={50} />
          ) : (
            "Entrar"
          )}
        </button>
      </Form>
      <Redirection to="/cadastro">NÃ£o tem conta ? Cadastra-se</Redirection>
    </Container>
  );
}
